import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const ShufflingCards = () => {
  const [cards, setCards] = useState([0, 1, 2, 3]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCards(prev => {
        const [first, ...rest] = prev;
        return [...rest, first]; // Move the first card to the end
      });
    }, 2000); // Shuffle every 2 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="mt-8 grid grid-cols-2 sm:grid-cols-2 md:grid-cols-4 gap-4">
      <AnimatePresence>
        {cards.map((card, index) =>
          <motion.div
            key={card}
            layout
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            transition={{ duration: 0.5 }}
            className="bg-black bg-opacity-40 h-[300px] w-[300px] sm:h-[200px] sm:w-[200px] md:h-[250px] md:w-[250px] rounded-lg p-4 flex flex-col items-center text-center"
          >
            <div className="bg-blue-600 w-12 h-12 rounded-full flex items-center justify-center">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="h-6 w-6 text-white"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                strokeWidth="2"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M8.5 12.5L12 15.5l3.5-3"
                />
              </svg>
            </div>
            <h3 className="text-lg font-bold mt-4">WiFi Router</h3>
            <p className="text-sm text-gray-300">TP Link Router</p>
            <p className="text-sm text-gray-400 mt-1">OFF</p>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default ShufflingCards;
