import React from 'react';
import header from "./images/header.png";
import voice from "./images/voice-img.png";
import image1 from "./images/voice-sign.png";
import audio from "./images/audio-design.png";
import { Navbar } from './components/Navbar';
import ShufflingCards from './components/ShufflingCards';

const App = () => {
  return (
    <div className="bg-gradient-to-br from-[#204660] to-[#5E3C8B] min-h-screen flex flex-col items-center justify-center text-white font-rajdhani ">
      {/* Header */}
      <header className="absolute top-6 left-6 flex items-center space-x-2">
        <img
          src={header}
          alt="header-logo"
          className="h-16 w-32 sm:h-20 sm:w-40 md:h-24 md:w-48 lg:h-30 lg:w-60"
        />
      </header>

      {/* Join Button */}
      <button className="flex items-center justify-center bg-black bg-opacity-40 font-semibold backdrop-blur-md px-4 py-2 text-sm sm:text-lg md:text-xl lg:text-2xl rounded-full my-10">
        <img
          src={image1}
          alt="voice-sign"
          className="h-10 w-10 sm:h-14 sm:w-14 md:h-16 md:w-16 lg:h-20 lg:w-20"
        />
        <span className="ml-4">Join the Conversation</span>
      </button>

      {/* Main Content */}
      <main className="text-center font-zenDots flex flex-col items-center justify-center">
        <h1 className="text-3xl sm:text-4xl md:text-5xl font-bold mb-2 bg-clip-text text-transparent bg-gradient-to-r from-[#9F62ED] via-[#FFFFFF] to-[#3AAEF8]">
          Speak. Share. Earn.
        </h1>
        <p className="text-sm sm:text-lg md:text-xl mb-8">
          Redefining Conversations with Voice.
        </p>
        <div>
          <img
            src={voice}
            alt="voice-img"
            className="h-40 w-40 sm:h-48 sm:w-48 md:h-56 md:w-56 lg:h-60 lg:w-60"
          />
        </div>

        {/* Navigation */}
        <Navbar />
      </main>

      {/* Why Choose Us Section */}
      <section className="mt-16 flex  items-center justify-center space-y-8">
        <div className="flex flex-col items-center justify-center">
          <p className="text-3xl sm:text-4xl md:text-5xl font-zenDots font-bold mb-2 bg-clip-text text-transparent bg-gradient-to-r from-[#9F62ED] via-[#FFFFFF] to-[#3AAEF8]">
            Why Choose Us?
          </p>
          <img
            src={audio}
            alt="audio-design"
            className="h-24 w-24 sm:h-32 sm:w-32 md:h-36 md:w-36 lg:h-40 lg:w-40"
          />
        </div>

        {/* Shuffling Cards Component */}
        <div className="w-full flex justify-center">
          <ShufflingCards />
        </div>
      </section>
    </div>
  );
};

export default App;
