import { motion } from "framer-motion";
import { useState, useEffect } from "react";

const ShufflingCards = () => {
  const [positions, setPositions] = useState([
    { id: 1, x: 0, y: 0 },
    { id: 2, x: 150, y: 0 },
    { id: 3, x: 150, y: 150 },
    { id: 4, x: 0, y: 150 }
  ]);

  useEffect(() => {
    const interval = setInterval(() => {
      setPositions(prev => {
        // Rotate the positions clockwise
        return [
          prev[3], // Move last card to first
          prev[0],
          prev[1],
          prev[2]
        ];
      });
    }, 2000); // Rotate every 2 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative h-[300px] w-[300px] sm:h-[200px] sm:w-[200px] md:h-[250px] md:w-[250px]">
      {positions.map((pos, index) =>
        <motion.div
          key={pos.id}
          animate={{
            x: pos.x,
            y: pos.y
          }}
          transition={{ duration: 0.8, ease: "easeInOut" }}
          className="absolute bg-black bg-opacity-40 h-[120px] w-[120px] sm:h-[100px] sm:w-[100px] md:h-[110px] md:w-[110px] rounded-lg p-4 flex flex-col items-center text-center"
        >
          <div className="bg-blue-600 w-12 h-12 rounded-full flex items-center justify-center">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6 text-white"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth="2"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M8.5 12.5L12 15.5l3.5-3"
              />
            </svg>
          </div>
          <h3 className="text-lg font-bold mt-4">
            WiFi Router {pos.id}
          </h3>
          <p className="text-sm text-gray-300">TP Link Router</p>
          <p className="text-sm text-gray-400 mt-1">OFF</p>
        </motion.div>
      )}
    </div>
  );
};

export default ShufflingCards;
